-- ----------------------------
-- Procedure structure for `p_update_order_status`
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_update_order_status`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `p_update_order_status`(`p_orderid` int,`p_status` varchar(200),`p_userid` int)
BEGIN
	
  DECLARE v_count INT DEFAULT 0;
  DECLARE v_gap_flag INT DEFAULT 0;
	DECLARE v_error INTEGER DEFAULT 0;

  DECLARE v_goods_id INT DEFAULT 0;
  DECLARE v_needs_sum INT DEFAULT 0;

	declare v_done int default false; 
	declare cur cursor for select goods_id,needs_sum from v_order_detail_sum where order_id = p_orderid;  
	declare continue HANDLER for not found set v_done = true;  

	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET v_error=1;

  SELECT 1 FROM `order`
	 WHERE id = p_orderid
	 limit 1
	  into v_count;
  
  IF (v_count > 0) THEN

		IF (p_status = 'receive') THEN
			
			SELECT COUNT(1) FROM v_order_check
       WHERE `order_id` = p_orderid
				 AND gap < 0
				INTO v_gap_flag;
			
		END IF;

		IF (v_gap_flag > 0) THEN

			select 2 as res;  /*物料库存不足*/

		ELSE

			START TRANSACTION;

			UPDATE `ORDER` SET STATUS = p_status,update_by = p_userid WHERE ID = p_orderid;
			INSERT INTO `order_status` (`order_id`,`status`,`create_by`) VALUES (p_orderid,p_status,p_userid);	

			
			IF (p_status = 'done' or p_status = 'receive') THEN

				/*更新库存信息*/
				open cur; 
				read_loop:loop 
				fetch cur into v_goods_id,v_needs_sum;

				if v_done then  
					leave read_loop;      
				end if;
				
				if (p_status = 'receive') then
				/* 接收订单时冻结库存 */
					update inventory set quantity = quantity - v_needs_sum,locked = locked + v_needs_sum where goods_id = v_goods_id;
				end if;

				if (p_status = 'done') then
				/* 扣减冻结库存 */
					update inventory set locked = locked - v_needs_sum where goods_id = v_goods_id;
				end if;

				end loop;
				close cur;
			
			END IF;
	
			IF v_error = 1 THEN  
				ROLLBACK;  
				select 1 as res; /*数据库执行异常*/
			ELSE  
				COMMIT;
				select 0 as res; /*成功*/
			END IF; 
			
		END IF;

	ELSE
		select 9 as res; /*order信息不正确*/
  END IF;

END
;;
DELIMITER ;

