-- ----------------------------
-- Table structure for order
-- ----------------------------
DROP TABLE IF EXISTS `order`;
CREATE TABLE `order` (
`id`  int(10) NOT NULL AUTO_INCREMENT ,
`customer_id`  int(10) NOT NULL COMMENT '客户ID' ,
`vendor_id`  int(10) NOT NULL COMMENT '供货商ID' ,
`order_code`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '订单号' ,
`order_type`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '订单类型' ,
`order_serial_no`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '流水号' ,
`order_date`  timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '需求时间' ,
`address`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '收货方' ,
`pur_no`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '收货方采购订单号' ,
`contacts`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '收货方联系方式' ,
`dock`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '道口' ,
`status`  enum('create','receive','ready','check','send','delivery','done') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '订单状态' ,
`truck_id`  int(11) NULL DEFAULT NULL COMMENT '发货车辆ID' ,
`remark`  varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注' ,
`create_by`  int(10) NULL DEFAULT NULL ,
`create_at`  timestamp NULL DEFAULT CURRENT_TIMESTAMP ,
`update_by`  int(10) NULL DEFAULT NULL ,
`update_at`  timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP ,
`isvalid`  tinyint(1) NULL DEFAULT 1 ,
PRIMARY KEY (`id`),
INDEX `IN_VENDOR_ID` (`vendor_id`) USING BTREE ,
INDEX `IN_ORDER_CODE` (`order_code`, `order_serial_no`) USING BTREE 
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
COMMENT='发货单信息'
AUTO_INCREMENT=37

