-- ----------------------------
DROP FUNCTION IF EXISTS `f_check_order_byuser`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `f_check_order_byuser`(p_orderid int,p_userid int) RETURNS int(11)
BEGIN
	/*依据用户的岗位分配用于检查用户是否拥有操作当前订单的操作权限*/
	DECLARE v_status VARCHAR(200) DEFAULT null;
	DECLARE v_auth INT DEFAULT 0;

	SELECT status FROM `order`
	 WHERE id = p_orderid
	 limit 1
	  into v_status;
	
	if v_status is null THEN		
		return 0;	
	end if;

	if v_status = 'create' THEN 
		/*接收订单 库管或理货员*/
		SELECT count(1) FROM `person`
		 WHERE id = p_userid
			 and person_title in (5,6)
		 limit 1
			into v_auth;
	end if;

	if v_status = 'receive' THEN
		/*备货完成 库管或理货员*/
		SELECT count(1) FROM `person`
		 WHERE id = p_userid
			 and person_title in (5,6)
		 limit 1
			into v_auth;
	end if;

	if v_status = 'ready' THEN
		/*对点完成 发货员*/
		SELECT count(1) FROM `person`
		 WHERE id = p_userid
			 and person_title = 7
		 limit 1
			into v_auth;
	end if;

	if v_status = 'check' THEN
		/*发货完成 发货员*/
		SELECT count(1) FROM `person`
		 WHERE id = p_userid
			 and person_title = 7
		 limit 1
			into v_auth;
	end if;

	if v_status = 'send' THEN
		/*交货完成 跟单员*/
		SELECT count(1) FROM `person`
		 WHERE id = p_userid
			 and person_title = 8
		 limit 1
			into v_auth;
	end if;

	if v_status = 'delivery' THEN
		/*全部完成 客服*/
		SELECT count(1) FROM `person`
		 WHERE id = p_userid
			 and person_title = 4
		 limit 1
			into v_auth;
	end if;


  return v_auth;

END
;;
DELIMITER ;

