-- Procedure structure for p_send_order
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_send_order`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `p_send_order`(`p_orderids` varchar(2000),`p_truckid` int,`p_userid` int)
BEGIN
	
  DECLARE v_count INT DEFAULT 0;
	DECLARE v_orderids VARCHAR(2000);
	DECLARE v_orderid INT DEFAULT 0;
	DECLARE v_truckid INT DEFAULT 0;
	DECLARE v_auth_flag INT DEFAULT 0;

	DECLARE v_error INTEGER DEFAULT 0;
	declare v_done int default false; 
	declare cur cursor for select id from `order` where locate(CONCAT(',',`id`,','),v_orderids) > 0 and `status` = 'check';  
	declare continue HANDLER for not found set v_done = true;  
	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET v_error=1;

	SELECT CONCAT(',',p_orderids,',') INTO v_orderids;

	START TRANSACTION;
	open cur; 
	read_loop:loop 
	/*遍历订单*/
	fetch cur into v_orderid;
	if v_done then  
		leave read_loop;      
	end if;
	/*检查操作权限*/		
		select f_check_order_byuser(v_orderid,p_userid) into v_auth_flag;
		if (v_auth_flag > 0) then
			/*检查车辆信息*/		
			select id from `truck` where id = p_truckid and isvalid = 1 into v_truckid;
			if (v_truckid > 0) then
				/*更新订单数据*/
				UPDATE `ORDER` SET STATUS = 'send',truck_id = p_truckid,update_by = p_userid WHERE ID = v_orderid;
				INSERT INTO `order_status` (`order_id`,`status`,`create_by`) VALUES (v_orderid,'send',p_userid);	
			else
				select 9 into v_error; /*车辆信息不正确*/
				LEAVE read_loop;
			end if;
		else
			select 8 into v_error; /*没有操作权限*/
			LEAVE read_loop;
		end if;
	end loop;
	close cur;

	if v_error > 0 then 
		select v_error as res; 
		ROLLBACK;
	else 
		select 0 as res; 
		COMMIT;
	END IF; 

END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for p_update_order_status
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_update_order_status`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `p_update_order_status`(`p_orderid` int,`p_oldstatus` varchar(200),`p_newstatus` varchar(200),`p_userid` int)
BEGIN
	
  DECLARE v_count INT DEFAULT 0;
  DECLARE v_gap_flag INT DEFAULT 0;
	DECLARE v_auth_flag INT DEFAULT 0;
	DECLARE v_error INTEGER DEFAULT 0;

  DECLARE v_goods_id INT DEFAULT 0;
  DECLARE v_sends_sum INT DEFAULT 0;
  DECLARE v_receives_sum INT DEFAULT 0;

	declare v_done int default false; 
	declare cur cursor for select goods_id,sends_sum,receives_sum from v_order_detail_sum where order_id = p_orderid;  
	declare continue HANDLER for not found set v_done = true;  

	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET v_error=1;

  SELECT 1 FROM `order`
	 WHERE id = p_orderid
     and status = p_oldstatus
	 limit 1
	  into v_count;
  
  IF (v_count > 0) THEN

		select f_check_order_byuser(p_orderid,p_userid) into v_auth_flag;

		if (v_auth_flag > 0) then

			IF (p_newstatus = 'receive') THEN
				
				SELECT COUNT(1) FROM v_order_check
				 WHERE `order_id` = p_orderid
					 AND gap < 0
					INTO v_gap_flag;
				
			END IF;

			IF (v_gap_flag > 0) THEN

				select 2 as res;  /*物料库存不足*/

			ELSE

				START TRANSACTION;

				UPDATE `ORDER` SET STATUS = p_newstatus,update_by = p_userid WHERE ID = p_orderid;
				INSERT INTO `order_status` (`order_id`,`status`,`create_by`) VALUES (p_orderid,p_newstatus,p_userid);	
				
				IF (p_newstatus = 'done' or p_newstatus = 'receive') THEN

					/*更新库存信息*/
					open cur; 
					read_loop:loop 
					fetch cur into v_goods_id,v_sends_sum,v_receives_sum;

					if v_done then  
						leave read_loop;      
					end if;
					
					if (p_newstatus = 'receive') then
					/* 接收订单时冻结库存 */
						update inventory set quantity = quantity - v_sends_sum,locked = locked + v_sends_sum where goods_id = v_goods_id;
					end if;

					if (p_newstatus = 'done') then
					/* 扣减冻结库存 */
						update inventory set quantity = quantity + v_sends_sum - v_receives_sum, locked = locked - v_sends_sum where goods_id = v_goods_id;
					end if;

					end loop;
					close cur;
				
				END IF;
				
				IF v_error = 1 THEN  
					ROLLBACK;  
					select 1 as res; /*数据库执行异常*/
				ELSE  
					COMMIT;
					select 0 as res; /*成功*/
				END IF; 
				
			END IF;
		
		ELSE
			select 8 as res; /*没有操作权限*/
		END IF;	

	ELSE
		select 9 as res; /*order信息不正确*/
  END IF;

END
;;
DELIMITER ;



